CREATE OR REPLACE VIEW V_WS_JE_2003_DESIGNA AS
SELECT    FA.IDINSTITUCION,--FCATJTA3
          FA.IDFACTURACION,
          EJG.ANIO EJG_ANIO,
          EJG.NUMEJG EJG_NUMERO,
          D.ANIO DESIGNA_ANIO,
          D.CODIGO DESIGNA_CODIGO,
          AD.NUMEROASUNTO AS NUMERO_ACTUACION,
        'CAB' AS TIPO_REGISTRO_CAB
            , '003' AS CAB1_TIPO_INTERCAMBIO --003	Comunicación actuaciones Prof.
            , NULL AS CAB2_NUMERO_INTERCAMBIO --Numero secuencial (se rellena con el contador INTERCAMBIOICM)
            , NULL AS CAB3_ANIO_INTERCAMBIO --En el contador meteremos como prefijo el año (se rellena con el contador INTERCAMBIOICM)
            , '$$' AS GRUPO_1
        , 'AXP' AS TIPO_REGISTRO_EXP
            , '013' AS AXP1_TIPO_ACTUALIZACION
            , LPAD(EJG.NUMEJG, 8, '0') AS AXP2_NUM_EXPEDIENTE --OB Numero de EJG
            , LPAD(EJG.ANIO, 4, '0') AS AXP3_ANIO_EXPEDIENTE --NOB Año del EJG
            , LPAD(NVL(EJG.NUMERO_CAJG, '       '), 7, '0') AS AXP4_NUM_EXP_SERV --Numero CAJG de EJG
            , LPAD(NVL(TO_CHAR(EJG.ANIOCAJG), '    '), 4, '0') AS AXP5_ANIO_EXP_SERV --Año cajg del EJG
            , '$$' AS GRUPO_2
         , 'PRC' AS TIPO_REGISTRO_PRJ
              , RPAD(NVL((CASE
                   WHEN AD.IDJUZGADO IS NOT NULL
                   THEN (SELECT JUZ.CODIGOEXT
                         FROM SCS_JUZGADO JUZ, CEN_POBLACIONES POB
                         WHERE JUZ.IDPOBLACION = POB.IDPOBLACION
                         AND JUZ.IDJUZGADO = AD.IDJUZGADO
                         AND JUZ.IDINSTITUCION = AD.IDINSTITUCION_JUZG)
                   WHEN D.IDJUZGADO IS NOT NULL
                   THEN (SELECT JUZ.CODIGOEXT
                        FROM SCS_JUZGADO JUZ, CEN_POBLACIONES POB
                         WHERE JUZ.IDPOBLACION = POB.IDPOBLACION
                         AND JUZ.IDJUZGADO = D.IDJUZGADO
                         AND JUZ.IDINSTITUCION = D.IDINSTITUCION_JUZG)
                   ELSE ' '
                   END), ' '), 10, ' ') AS PRC1_ORGANO_JUDICIAL
              /*, RPAD((CASE
                      WHEN AD.IDPRETENSION IS NOT NULL
                      THEN (SELECT NVL(PRE.CODIGOEXT, ' ')
                                   FROM SCS_PRETENSION PRE
                                   WHERE PRE.IDINSTITUCION = AD.IDINSTITUCION AND PRE.IDPRETENSION = AD.IDPRETENSION)
                      WHEN D.IDPRETENSION IS NOT NULL
                      THEN (SELECT NVL(PRE.CODIGOEXT, ' ')
                                   FROM SCS_PRETENSION PRE
                                   WHERE PRE.IDINSTITUCION = D.IDINSTITUCION AND PRE.IDPRETENSION = D.IDPRETENSION)
                      ELSE ' '
                      END), 3, ' ') AS PRC2_TIPO_PROCED_JUDICIAL*/
              , RPAD((CASE
                      WHEN AD.IDPROCEDIMIENTO IS NOT NULL
                      THEN (SELECT NVL(PRO.CODIGOEXT, ' ')
                                   FROM SCS_PROCEDIMIENTOS PRO
                                   WHERE PRO.IDINSTITUCION = AD.IDINSTITUCION AND PRO.IDPROCEDIMIENTO = AD.IDPROCEDIMIENTO)
                      ELSE ' '
                      END), 3, ' ') AS PRC2_TIPO_PROCED_JUDICIAL
              , LPAD(NVL((CASE
                    WHEN AD.NUMEROPROCEDIMIENTO IS NOT NULL
                    THEN (CASE INSTR(AD.NUMEROPROCEDIMIENTO, '/')
                            WHEN 0 THEN NULL
                            ELSE SUBSTR(AD.NUMEROPROCEDIMIENTO, 0, INSTR(AD.NUMEROPROCEDIMIENTO, '/')-1)
                          END)
                    WHEN D.NUMPROCEDIMIENTO IS NOT NULL
                    THEN (CASE INSTR(D.NUMPROCEDIMIENTO, '/')
                            WHEN 0 THEN NULL
                            ELSE SUBSTR(D.NUMPROCEDIMIENTO, 0, INSTR(D.NUMPROCEDIMIENTO, '/')-1)
                          END)
                    ELSE NULL
                   END), '       '), 7, '0') AS PRC3_NUM_PROCEDIMIENTO
               , LPAD(NVL((CASE
                    WHEN INSTR(AD.NUMEROPROCEDIMIENTO, '/20') > 0
                    THEN RPAD(SUBSTR(AD.NUMEROPROCEDIMIENTO, INSTR(AD.NUMEROPROCEDIMIENTO, '/')+1, 4), 4, '0')
                    WHEN INSTR(AD.NUMEROPROCEDIMIENTO, '/') > 0
                    THEN RPAD('20' || SUBSTR(AD.NUMEROPROCEDIMIENTO, INSTR(AD.NUMEROPROCEDIMIENTO, '/')+1, 2), 4, '0')
                    WHEN INSTR(D.NUMPROCEDIMIENTO, '/20') > 0
                    THEN RPAD(SUBSTR(D.NUMPROCEDIMIENTO, INSTR(D.NUMPROCEDIMIENTO, '/')+1, 4), 4, '0')
                    WHEN INSTR(D.NUMPROCEDIMIENTO, '/') > 0
                    THEN RPAD('20' || SUBSTR(D.NUMPROCEDIMIENTO, INSTR(D.NUMPROCEDIMIENTO, '/')+1, 2), 4, '0')
                    ELSE NULL
                    END), '    '), 4, '0') AS PRC4_ANIO_PROCEDIMIENTO
              , RPAD(NVL((SELECT P.CODIGO FROM PCAJG_TIPO_EJG P, PCAJG_TIPO_EJG_SCSTIPOEJG PS
                        WHERE P.IDENTIFICADOR = PS.IDENTIFICADOR
                        AND P.IDINSTITUCION = PS.IDINSTITUCION
                        AND P.IDINSTITUCION = EJG.IDINSTITUCION
                        AND PS.IDTIPOEJG = EJG.IDTIPOEJG), ' '), 3, ' ') AS PRC5_NATURALEZA_PROCEDIMIENTO
              , LPAD(NVL((SELECT TC.CODIGO FROM SCS_TIPOENCALIDAD TC WHERE TC.IDTIPOENCALIDAD = EJG.IDTIPOENCALIDAD AND TC.IDINSTITUCION = EJG.CALIDADIDINSTITUCION), '   '), 3, ' ') AS PRC6_ESTADO_PROCEDIMIENTO
              , '0' AS PRC7_RESOLUCION_PROCEDIMIENTO -- 0 = Sin condena a costas a la parte contraria 1 = Condena a costas a la parte contraria
              --ENVIAMOS EL MOTIVO DEL CAMBIO SI EL CÓDIGO EXT DE LA PRETENSION DE LA DESIGNA ES DIFERENTE DEL CODIGO EXT DEL MODULO DE LA ACTUACION
              , (CASE WHEN (SELECT PRE.CODIGOEXT FROM SCS_PRETENSION PRE WHERE PRE.IDINSTITUCION = D.IDINSTITUCION AND PRE.IDPRETENSION = D.IDPRETENSION)
                           <> (SELECT PRO.CODIGOEXT FROM SCS_PROCEDIMIENTOS PRO WHERE PRO.IDINSTITUCION = AD.IDINSTITUCION AND PRO.IDPROCEDIMIENTO = AD.IDPROCEDIMIENTO)
                           AND AD.ID_MOTIVO_CAMBIO IS NOT NULL
                           THEN (SELECT LPAD(NVL(MC.CODIGOEXT, ' '), 1, ' ')
                                FROM SCS_ACTUADESIG_MOTCAMBIO MC
                                WHERE MC.IDINSTITUCION = AD.IDINSTITUCION
                                AND MC.IDACTDESMOTCAMBIO = AD.ID_MOTIVO_CAMBIO)
                           ELSE ' '
                     END) AS PRC8_MOTIVO_MODIFICACION
              , '$$' AS GRUPO_3
          , 'DPA' AS TIPO_REGISTRO_PFA
              , 'A' AS DPA1_ABOGADO_PROCURADOR
              , '28005' AS DPA2_COLEGIO_PROFESIONAL
              , RPAD(DECODE(DLN.IDPERSONA, NULL, ' ', NVL(F_SIGA_CALCULONCOLEGIADO(DLN.IDINSTITUCION, DLN.IDPERSONA), ' ')), 6, ' ') AS DPA3_NCOLEGIADOABOGADO
              , RPAD(NVL(TO_CHAR(DLN.FECHADESIGNA, 'YYYYMMDD'), ' '), 8, ' ') AS DPA4_FECHA_DESIGNA
              , (SELECT COUNT(1) NUMERO --CONTAMOS LOS LETRADOS PREVIOS CON FECHARENUNCIA DISTINTO DE NULL PARA PONER UN PREFIJO
                        FROM SCS_DESIGNASLETRADO DESLET2
                        WHERE DLN.IDINSTITUCION = DESLET2.IDINSTITUCION
                        AND DLN.IDTURNO = DESLET2.IDTURNO
                        AND DLN.ANIO = DESLET2.ANIO
                        AND DLN.NUMERO = DESLET2.NUMERO
                        AND DESLET2.FECHARENUNCIA IS NOT NULL) || LPAD(TO_CHAR(D.CODIGO), 5, '0') AS DPA5_NUMERO_DESIGNA
              , LPAD(NVL(TO_CHAR(D.ANIO), '    '), 4, '0') AS DPA6_ANIO_DESIGNA
              , '$$' AS GRUPO_4
          , 'DAC' AS TIPO_REGISTRO_DAC
              , LPAD(NVL(AP.CODIGOEXT, '   '), 3, '0') AS DAC1_TIPO_ACTUACION_SUPLEM
              , LPAD(NVL(TO_CHAR(AD.FECHA, 'YYYYMMDD'), ' '), 8, ' ') AS DAC2_FECHA_ACTUACION_SUPLEM
              , LPAD(' ', 3, ' ') AS DAC3_NUMERO_SUPLEMENTOS
              , LPAD((CASE
                      WHEN AP.CODIGOEXT LIKE '007%' OR AP.CODIGOEXT LIKE '008%'
                      THEN TO_CHAR(AD.FECHA, 'YYYYMMDD')
                      ELSE ' '
                      END), 8, ' ') AS DAC4_FECHA_VISITA_PRISION--SI ES 007 O 008 PONER FECHA DE LA ACTUACIÓN
               , LPAD(NVL(AP.CODSUBTARIFA, '   '), 3, '0') AS DAC5_SUBTARIFA
               , LPAD(ROUND(FA.PRECIOAPLICADO*FA.PORCENTAJEFACTURADO), 10, '0') AS DAC6_IMPORTE_ACTUACION
          , DECODE((SELECT COUNT(1) FROM CAJG_EJGREMESA ER, CAJG_REMESAESTADOS RE
                    WHERE EJG.IDINSTITUCION = ER.IDINSTITUCION
                    AND EJG.ANIO = ER.ANIO
                    AND EJG.IDTIPOEJG = ER.IDTIPOEJG
                    AND EJG.NUMERO = ER.NUMERO
                    AND ER.IDINSTITUCION = RE.IDINSTITUCION
                    AND ER.IDREMESA = RE.IDREMESA
                    AND RE.IDESTADO = 2 --REMESA ENVIADA
                    AND NOT EXISTS (SELECT 1 FROM CAJG_RESPUESTA_EJGREMESA RER WHERE RER.IDEJGREMESA = ER.IDEJGREMESA))
                  , 0, 1, 0)  IS_ENVIADO_ENREMESA
           , (CASE WHEN (AD.ID_MOTIVO_CAMBIO IS NOT NULL OR AD.IDJUZGADO IS NULL OR (D.IDINSTITUCION_JUZG = AD.IDINSTITUCION_JUZG AND D.IDJUZGADO = AD.IDJUZGADO))
                   THEN 0
                   ELSE 1
                   END) AS IS_CAMBIO_JUZGADO
            --comparamos con el código de la pretensión pq es lo que se envía junto al expedinte a la CAM. si cambia luego el módulo de la actuación se debe indicar el motivo
           /*, (CASE WHEN (SELECT PRO.CODIGOEXT FROM SCS_PROCEDIMIENTOS PRO WHERE PRO.IDINSTITUCION = D.IDINSTITUCION AND PRO.IDPROCEDIMIENTO = D.IDPROCEDIMIENTO)
                           <> (SELECT PRO.CODIGOEXT FROM SCS_PROCEDIMIENTOS PRO WHERE PRO.IDINSTITUCION = AD.IDINSTITUCION AND PRO.IDPROCEDIMIENTO = AD.IDPROCEDIMIENTO)
                           AND AD.ID_MOTIVO_CAMBIO IS NULL
                   THEN 1
                   ELSE 0
--           , (CASE WHEN (AD.ID_MOTIVO_CAMBIO IS NOT NULL OR (D.IDINSTITUCION = AD.IDINSTITUCION AND D.IDPRETENSION = AD.IDPRETENSION))   THEN 0       ELSE 1
                   END) AS IS_CAMBIO_PROCEDIMIENTO*/
           , (CASE WHEN (SELECT PRE.CODIGOEXT FROM SCS_PRETENSION PRE WHERE PRE.IDINSTITUCION = D.IDINSTITUCION AND PRE.IDPRETENSION = D.IDPRETENSION)
                           <> (SELECT PRE.CODIGOEXT FROM SCS_PRETENSION PRE WHERE PRE.IDINSTITUCION = AD.IDINSTITUCION AND PRE.IDPRETENSION = AD.IDPRETENSION)
                           AND AD.ID_MOTIVO_CAMBIO IS NULL
                   THEN 0
                   ELSE 0
--           , (CASE WHEN (AD.ID_MOTIVO_CAMBIO IS NOT NULL OR (D.IDINSTITUCION = AD.IDINSTITUCION AND D.IDPRETENSION = AD.IDPRETENSION))   THEN 0       ELSE 1
                   END) AS IS_CAMBIO_PROCEDIMIENTO
            , (CASE
                  WHEN EJG.ANIO < 2012
                  THEN 1
                  ELSE 0
                  END) AS IS_EJG_ANTERIOR_2012
            , (CASE
                  WHEN AD.FECHAJUSTIFICACION < AD.FECHA
                  THEN 1
                  ELSE 0
                  END) AS IS_FECHA_VALID_ANTER_ACTUAC
            , (CASE
                  WHEN EJG.IDTIPORATIFICACIONEJG IS NULL
                  THEN 1
                  ELSE 0
                  END) AS IS_PENDIENTE_RESOLV_COMIS
